rss.script:
var TAS_SITE = angular.module('TAS_SITE', ['ngRoute',   'RSS']);
TAS_SITE.config(['$routeProvider',
  function($routeProvider) {
   $routeProvider.

          when('/alumni_spot_rss',{
     	//templateUrl:'partials/rss.xml',
     	controller:'rssController'

     }) . 
    otherwise({
      	templateUrl: 'partials/home.html',
        redirectTo: '/home/'
        //controller:'partials/home.html'
     });






     // use the HTML5 History API


  }]);
TAS_SITE.factory('CreateRSS', ['$http', '$routeParams', '$location', '$rootScope', '$sce',
function($http, $routeParams, $location, $rootScope, $sce) {

	return {

		rssCreate : function(url) {
			var spot = [];
			return $http.jsonp(url).then(function(result) {
				if (result.data.rows != null) {
					var d = new Date();
					td = d.valueOf();

					result.data.rows.reverse();
					for (var i = 0; i < result.data.rows.length; i++) {
						var pd = new Date(result.data.rows[i][6]);
						var tpd = pd.valueOf();

						if (result.data.rows[i][6] != '#' && td >= tpd ) {
							spot.push({
								id : i,
								firstname : result.data.rows[i][0],
								lastname : result.data.rows[i][1],
								shortbody : result.data.rows[i][2],
								longbody : result.data.rows[i][3],
								image : result.data.rows[i][4].split('?')[0],
								caption : result.data.rows[i][5],
								date : result.data.rows[i][6],
								region : result.data.rows[i][7],
								more_url : result.data.rows[i][5].replace(/ /g, '_'),
								hash : '/indiv_spotlight/' + result.data.rows[i][0].replace(/ /g, '_') + '_' + result.data.rows[i][1].replace(/ /g, '_'),
								dataloaded : true,
								hider : true
							});

						}

					}

				}
				return spot;
			});

		},
	};
}]);
angular.module('RSS', [])
.controller('rssController', ['$scope','CreateRSS', function($scope, CreateRSS)
{

	CreateRSS.rssCreate('https://www.googleapis.com/fusiontables/v1/query?sql=SELECT+FirstName,LastName,ShortBody,LongBody,image,caption,PublishDate,Region+FROM+1z6kUehyfSNqaAGinvARZLYyjb7Dhk2F9rt49xHIV+ORDER%20BY+PublishDate+%22&key=AIzaSyBBcCEirvYGEa2QoGas7w2uaWQweDF2pi0&callback=JSON_CALLBACK').then(function(data){
		alert('creating')
		$scope.data=data;
		console.log(data);
	});





}]);
